import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  TextField,
  Typography,
  Container,
  Button,
  Select,
  MenuItem,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  CardActions,
  makeStyles,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { listProducts } from "../actions/productActions";
import Rating from "../components/Rating";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 200,
    width: 150,
  },
});
function HomeScreen(props) {
  const [searchKeyword, setSearchKeyword] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const category = props.match.params.id ? props.match.params.id : "";
  const productList = useSelector((state) => state.productList);
  const { products, loading, error } = productList;
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listProducts(category));

    return () => {
      //
    };
  }, [category]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const sortHandler = (e) => {
    setSortOrder(e.target.value);
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const classes = useStyles();
  return (
    <Container maxWidth="lg">
      {category && <Typography variant="h2">{category}</Typography>}

      <ul className="filter">
        <li>
          <form onSubmit={submitHandler}>
            <TextField
              name="searchKeyword"
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <Button color="primary" variant="contained" type="submit">
              Search
            </Button>
          </form>
        </li>
        <li>
          Sort By
          <Select
            name="sortOrder"
            onChange={sortHandler}
            inputProps={{
              name: "age",
              id: "age-native-simple",

            }}
          >
            <MenuItem value="Newest">Newest</MenuItem>
            <MenuItem value="Lowest">Lowest</MenuItem>
            <MenuItem value="Highest">Highest</MenuItem>
          </Select>
          </li>
      </ul>

      <div className="my-5 text-center container">
  <div className="row mb-2">
    <div className="col text-center">
      <h1>Carousel with outer controls</h1>
      <p className="lead">Does what it says on the tin.</p>
    </div>
  </div>
  <div className="row d-flex align-items-center">
    <div className="col-1 d-flex align-items-center justify-content-center">
      <a href="#carouselExampleIndicators" role="button" data-slide="prev">
        <div className="carousel-nav-icon">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 129 129" xmlnsXlink="http://www.w3.org/1999/xlink">
            <path d="m88.6,121.3c0.8,0.8 1.8,1.2 2.9,1.2s2.1-0.4 2.9-1.2c1.6-1.6 1.6-4.2 0-5.8l-51-51 51-51c1.6-1.6 1.6-4.2 0-5.8s-4.2-1.6-5.8,0l-54,53.9c-1.6,1.6-1.6,4.2 0,5.8l54,53.9z" />
          </svg>
        </div>
      </a>
    </div>
    <div className="col-10">
      {/*Start carousel*/}
      <div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
        <div className="carousel-inner">
          <div className="carousel-item active">
            <div className="row">
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun04.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
              </div>
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun02.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
              </div>
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun05.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
                <h3 className="text-white">Text in the panel.</h3>
              </div>
            </div>
          </div>
          <div className="carousel-item">
            <div className="row">
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun01.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
              </div>
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun03.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
              </div>
              <div style={{backgroundImage: 'url("https://s3-us-west-2.amazonaws.com/s.cdpn.io/46992/flexfun06.jpg")'}} className="col-12 col-md d-flex align-items-center justify-content-center">
              </div>
            </div>
          </div>
        </div>
      </div>
      {/*End carousel*/}
    </div>
    <div className="col-1 d-flex align-items-center justify-content-center"><a href="#carouselExampleIndicators" data-slide="next">
        <div className="carousel-nav-icon">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 129 129" xmlnsXlink="http://www.w3.org/1999/xlink">
            <path d="m40.4,121.3c-0.8,0.8-1.8,1.2-2.9,1.2s-2.1-0.4-2.9-1.2c-1.6-1.6-1.6-4.2 0-5.8l51-51-51-51c-1.6-1.6-1.6-4.2 0-5.8 1.6-1.6 4.2-1.6 5.8,0l53.9,53.9c1.6,1.6 1.6,4.2 0,5.8l-53.9,53.9z" />
          </svg>
        </div>
      </a>
    </div>
  </div>
</div>


      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ul className="products">
          {products.map((product) => (




             <Card key={product._id}>
             <CardActionArea>
            <img
                  src={product.image}
                  title={product.name}
                  width="200"
                  height="300"
               />
                <CardContent>
                <Typography gutterBottom variant="h3" component="h5">
                    {product.brand}
                  </Typography>

                  <Typography gutterBottom variant="h5" component="h2">
                    {product.name}
                  </Typography>

                  <Typography gutterBottom variant="h3" component="h5">
                    Rs.{product.price}
                  </Typography>

                  <Rating
                    value={product.rating}
                    text={product.numReviews + " reviews"}
                  />
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  <Link to={"/product/" + product._id}>{product.name}</Link>
                </Button>
              </CardActions>
            </Card>



          ))}
        </ul>
      )}
    </Container>
  );
}
export default HomeScreen;
