import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  TextField,
  Typography,
  Container,
  Button,
  Select,
  MenuItem,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  CardActions,
  makeStyles,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { listProducts } from "../actions/productActions";
import Rating from "../components/Rating";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 200,
    width: 150,
  },
});
function HomeScreen(props) {
  const [searchKeyword, setSearchKeyword] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const category = props.match.params.id ? props.match.params.id : "";
  const productList = useSelector((state) => state.productList);
  const { products, loading, error } = productList;
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listProducts(category));

    return () => {
      //
    };
  }, [category]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const sortHandler = (e) => {
    setSortOrder(e.target.value);
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const classes = useStyles();
  return (
    <Container maxWidth="lg">
      {category && <Typography variant="h2">{category}</Typography>}

      <ul className="filter">
        <li>
          <form onSubmit={submitHandler}>
            <TextField
              name="searchKeyword"
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <Button color="primary" variant="contained" type="submit">
              Search
            </Button>
          </form>



        </li>
        <li>
          Sort By
          <Select
            name="sortOrder"
            onChange={sortHandler}
            inputProps={{
              name: "age",
              id: "age-native-simple",

            }}
          >
            <MenuItem value="Newest">Newest</MenuItem>
            <MenuItem value="Lowest">Lowest</MenuItem>
            <MenuItem value="Highest">Highest</MenuItem>
          </Select>
          </li>
      </ul>

      <div className="container py-5">
          <header className="text-center mb-5">
          <h1 className="display-4 font-weight-bold">THRIFTED.CO</h1>
          <p className="font-italic">Home to the best in vintage clothing.</p>
          </header>
      </div>

      <>
  <meta charSet="utf-8" />
  <meta httpEquiv="X-UA-Compatible" content="IE=edge" />
  <meta name="viewport" content="width=device-width, initial-scale=1" />
  <title>Bootstrap Elegant Image Slider</title>
  <link href="https://fonts.googleapis.com/css?family=Oswald:300,400|Roboto|Open+Sans:300,400,700" rel="stylesheet" />
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" />
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css" />
  <style dangerouslySetInnerHTML={{__html: "\n.carousel {\n\tmargin: 25px 0 50px;\n    background: #fff;\n\tposition: relative;\n\tpadding: 8px;\n\tbox-shadow: 0 0 1px rgba(0,0,0,0.3);\n}\n.carousel:before, .carousel:after {\n\tz-index: -1; \n\tposition: absolute; \n\tcontent: \"\";\n\tbottom: 15px;\n\tleft: 10px;\n\twidth: 50%; \n\ttop: 80%;\n\tmax-width: 400px;\n\tbackground: rgba(0, 0, 0, 0.7); \n\tbox-shadow: 0 15px 10px rgba(0, 0, 0, 0.7);\n\ttransform: rotate(-3deg);\n}\n.carousel:after {\n  transform: rotate(3deg);\n  right: 10px;\n  left: auto;\n}\n.carousel .item {\t\n    text-align: center;\n\tmin-height: 314px;\n}\n.carousel .item img {\n\tmax-width: 100%;\n    margin: 0 auto; /* Align slide image horizontally center in Bootstrap v3 */\n}\n.carousel .carousel-control {\n\twidth: 27px;\n\theight: 54px;\n\toverflow: hidden;\n\topacity: 1;\n\tmargin: auto 0;\n\tbackground: none;\n\ttext-shadow: none;\n}\n.carousel .carousel-control span {\n\twidth: 54px;\n    height: 54px;\n\tdisplay: inline-block;\n    background: #4a4a4a;    \n    border-radius: 50%;\n    box-shadow: 0 0 1px rgba(0,0,0,0.3);\n}\n.carousel .carousel-control.left span {\n\tmargin-right: -27px;\n}\n.carousel .carousel-control.right span {\n\tmargin-left: -27px;\n}\n.carousel .carousel-control:hover span {\n\tbackground: #3b3b3b;\n}\n.carousel .carousel-control i {\n    font-size: 24px;\n    margin-top: 13px;\n}\n.carousel .carousel-control.left {\n\tmargin-left: -28px;\n}\n.carousel .carousel-control.right {\n\tmargin-right: -28px;\n}\n.carousel .carousel-control.left i {\n\tmargin-left: -24px;\n}\n.carousel .carousel-control.right i {\n\tmargin-right: -24px;\n}\n.carousel-indicators {\n\tbottom: -42px;\n}\n.carousel-indicators li, .carousel-indicators li.active {\n\twidth: 11px;\n\theight: 11px;\n\tborder-radius: 50%;\n\tmargin: 1px 4px;\n}\n.carousel-indicators li {\n\tborder: 1px solid #475c6f;\n}\n.carousel-indicators li.active {\n\tbackground: #20b0b9;\n\tborder-color: #20b0b9;\n}\n" }} />
  <div className="container">
    <div className="row">
      <div className="col-md-8 col-md-offset-2">
        <div id="myCarousel" className="carousel slide" data-ride="carousel">
          {/* Carousel indicators */}
          <ol className="carousel-indicators">
            <li data-target="#myCarousel" data-slide-to={0} className="active" />
            <li data-target="#myCarousel" data-slide-to={1} />
            <li data-target="#myCarousel" data-slide-to={2} />
