import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  TextField,
  Typography,
  Container,
  Button,
  Select,
  MenuItem,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  CardActions,
  makeStyles,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { listProducts } from "../actions/productActions";
import Rating from "../components/Rating";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 200,
    width: 150,
  },
});
function HomeScreen(props) {
  const [searchKeyword, setSearchKeyword] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const category = props.match.params.id ? props.match.params.id : "";
  const productList = useSelector((state) => state.productList);
  const { products, loading, error } = productList;
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listProducts(category));

    return () => {
      //
    };
  }, [category]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const sortHandler = (e) => {
    setSortOrder(e.target.value);
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const classes = useStyles();
  return (
    <Container maxWidth="lg">
      {category && <Typography variant="h2">{category}</Typography>}

      <ul className="filter">
        <li>
          <form onSubmit={submitHandler}>
            <TextField
              name="searchKeyword"
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <Button color="primary" variant="contained" type="submit">
              Search
            </Button>
          </form>



        </li>
        <li>
          Sort By
          <Select
            name="sortOrder"
            onChange={sortHandler}
            inputProps={{
              name: "age",
              id: "age-native-simple",

            }}
          >
            <MenuItem value="Newest">Newest</MenuItem>
            <MenuItem value="Lowest">Lowest</MenuItem>
            <MenuItem value="Highest">Highest</MenuItem>
          </Select>
          </li>
      </ul>



      <div id="carouselExampleControls" className="carousel slide" data-ride="carousel">
      <div className="container py-5">
          <header className="text-center mb-5">
          <h1 className="display-4 font-weight-bold">Home to the best in vintage clothing.</h1>
          <p className="font-italic">THRIFT.CO</p>
          </header>
      </div>
  <div className="carousel-inner">
    <div className="carousel-item active">
      <img className="d-block w-100" src="https://lp2.hm.com/hmgoepprod?set=width[1280],quality[80],options[limit]&source=url[https://www2.hm.com/content/dam/ladies_s03/magazine-2021/1143-3x2-Magazine-Landscape-1688x1126-72ppi-6.jpg]&scale=width[global.width],height[15000],options[global.options]&sink=format[jpg],quality[global.quality]" alt="First slide" />
    </div>
    <div className="carousel-item">
      <img className="d-block w-100" src="https://lp2.hm.com/hmgoepprod?source=url[https://www2.hm.com/content/dam/ladies_s03/magazine-2021/1103-3x2-Magazine-Landscape-1688x1126_1.jpg]&sink=format[jpeg],quality[70]" alt="Second slide" />
    </div>
    <div className="carousel-item">
      <img className="d-block w-100" src="..." alt="Third slide" />
    </div>
  </div>
  <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
    <span className="carousel-control-prev-icon" aria-hidden="true" />
    <span className="sr-only">Previous</span>
  </a>
  <a className="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
    <span className="carousel-control-next-icon" aria-hidden="true" />
    <span className="sr-only">Next</span>
  </a>
</div>



      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ul className="products">
          {products.map((product) => (




             <Card key={product._id}>
             <CardActionArea>
            <img
                  src={product.image}
                  title={product.name}
                  width="200"
                  height="300"
               />
                <CardContent>
                <Typography gutterBottom variant="h3" component="h5">
                    {product.brand}
                  </Typography>

                  <Typography gutterBottom variant="h5" component="h2">
                    {product.name}
                  </Typography>

                  <Typography gutterBottom variant="h3" component="h5">
                    Rs.{product.price}
                  </Typography>

                  <Rating
                    value={product.rating}
                    text={product.numReviews + " reviews"}
                  />
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  <Link to={"/product/" + product._id}>{product.name}</Link>
                </Button>
              </CardActions>
            </Card>



          ))}
        </ul>
      )}
    </Container>
  );
}
export default HomeScreen;
