import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import {
  TextField,
  Typography,
  Container,
  Button,
  Select,
  MenuItem,
  Card,
  CardActionArea,
  CardMedia,
  CardContent,
  CardActions,
  makeStyles,
} from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { listProducts } from "../actions/productActions";
import Rating from "../components/Rating";

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 200,
    width: 150,
  },
});
function HomeScreen(props) {
  const [searchKeyword, setSearchKeyword] = useState("");
  const [sortOrder, setSortOrder] = useState("");
  const category = props.match.params.id ? props.match.params.id : "";
  const productList = useSelector((state) => state.productList);
  const { products, loading, error } = productList;
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(listProducts(category));

    return () => {
      //
    };
  }, [category]);

  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const sortHandler = (e) => {
    setSortOrder(e.target.value);
    dispatch(listProducts(category, searchKeyword, sortOrder));
  };
  const classes = useStyles();
  return (
    <Container maxWidth="lg">
      {category && <Typography variant="h2">{category}</Typography>}

      <ul className="filter">
        <li>
          <form onSubmit={submitHandler}>
            <TextField
              name="searchKeyword"
              onChange={(e) => setSearchKeyword(e.target.value)}
            />
            <Button color="primary" variant="contained" type="submit">
              Search
            </Button>
          </form>



        </li>
        <li>
          Sort By
          <Select
            name="sortOrder"
            onChange={sortHandler}
            inputProps={{
              name: "age",
              id: "age-native-simple",

            }}
          >
            <MenuItem value="Newest">Newest</MenuItem>
            <MenuItem value="Lowest">Lowest</MenuItem>
            <MenuItem value="Highest">Highest</MenuItem>
          </Select>
          </li>
      </ul>



      <main>
  <div className="container">
    <div className="carousel slide" id="main-carousel" data-ride="carousel">
      <ol className="carousel-indicators">
        <li data-target="#main-carousel" data-slide-to={0} className="active" />
        <li data-target="#main-carousel" data-slide-to={1} />
        <li data-target="#main-carousel" data-slide-to={2} />
        <li data-target="#main-carousel" data-slide-to={3} />
      </ol>{/* /.carousel-indicators */}
      <div className="carousel-inner">
        <div className="carousel-item active">
          <img className="d-block img-fluid" src="https://s19.postimg.cc/qzj5uncgj/slide1.jpg" alt />
          <div className="carousel-caption d-none d-md-block">
            <h1>Mountain</h1>
          </div>
        </div>
        <div className="carousel-item">
          <img className="d-block img-fluid" src="https://s19.postimg.cc/lmubh3h0j/slide2.jpg" alt />
          <div className="carousel-caption d-none d-md-block">
            <h3>Mountain</h3>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Adipisci esse vitae exercitationem fugit, numquam minus!</p>
          </div>
        </div>
        <div className="carousel-item">
          <img className="d-block img-fluid" src="https://s19.postimg.cc/99hh9lr5v/slide3.jpg" alt />
          <div className="carousel-caption d-none d-md-block">
            <h3>Mountain</h3>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Adipisci esse vitae exercitationem fugit, numquam minus!</p>
          </div>
        </div>
        <div className="carousel-item">
          <img src="https://s19.postimg.cc/nenabzsnn/slide4.jpg" alt className="d-block img-fluid" />
          <div className="carousel-caption d-none d-md-block">
            <h3>Mountain</h3>
            <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Adipisci esse vitae exercitationem fugit, numquam minus!</p>
          </div>
        </div>
      </div>{/* /.carousel-inner */}
      <a href="#main-carousel" className="carousel-control-prev" data-slide="prev">
        <span className="carousel-control-prev-icon" />
        <span className="sr-only" aria-hidden="true">Prev</span>
      </a>
      <a href="#main-carousel" className="carousel-control-next" data-slide="next">
        <span className="carousel-control-next-icon" />
        <span className="sr-only" aria-hidden="true">Next</span>
      </a>
    </div>{/* /.carousel */}
  </div>{/* /.container */}
</main>




      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ul className="products">
          {products.map((product) => (




             <Card key={product._id}>
             <CardActionArea>
            <img
                  src={product.image}
                  title={product.name}
                  width="200"
                  height="300"
               />
                <CardContent>
                <Typography gutterBottom variant="h3" component="h5">
                    {product.brand}
                  </Typography>

                  <Typography gutterBottom variant="h5" component="h2">
                    {product.name}
                  </Typography>

                  <Typography gutterBottom variant="h3" component="h5">
                    Rs.{product.price}
                  </Typography>

                  <Rating
                    value={product.rating}
                    text={product.numReviews + " reviews"}
                  />
                </CardContent>
              </CardActionArea>
              <CardActions>
                <Button size="small" color="primary">
                  <Link to={"/product/" + product._id}>{product.name}</Link>
                </Button>
              </CardActions>
            </Card>



          ))}
        </ul>
      )}
    </Container>
  );
}
export default HomeScreen;
